dt-option.example:
$('#example').dataTable( {
  "stateSave": true,
  "stateLoadCallback": function (settings) {
    var o;

    // Send an Ajax request to the server to get the data. Note that
    // this is a synchronous request since the data is expected back from the
    // function
    $.ajax( {
      "url": "/state_load",
      "async": false,
      "dataType": "json",
      "success": function (json) {
        o = json;
      }
    } );

    return o;
  }
} );
