dt-example.js:
function filterGlobal ()
{
	$('#example').DataTable().search( 
		$("#global_filter").val(),
		$("#global_regex")[0].checked, 
		$("#global_smart")[0].checked
	);
}

function filterColumn ( i )
{
	$('#example').DataTable().column( i ).search( 
		$("#col"+(i+1)+"_filter").val(),
		$("#col"+(i+1)+"_regex")[0].checked, 
		$("#col"+(i+1)+"_smart")[0].checked
	);
}

$(document).ready(function() {
	$('#example').dataTable();

	$("#global_filter").keyup( filterGlobal );
	$("#global_regex").click( filterGlobal );
	$("#global_smart").click( filterGlobal );

	$("#col1_filter").keyup( function() { filterColumn( 0 ); } );
	$("#col1_regex").click(  function() { filterColumn( 0 ); } );
	$("#col1_smart").click(  function() { filterColumn( 0 ); } );

	$("#col2_filter").keyup( function() { filterColumn( 1 ); } );
	$("#col2_regex").click(  function() { filterColumn( 1 ); } );
	$("#col2_smart").click(  function() { filterColumn( 1 ); } );

	$("#col3_filter").keyup( function() { filterColumn( 2 ); } );
	$("#col3_regex").click(  function() { filterColumn( 2 ); } );
	$("#col3_smart").click(  function() { filterColumn( 2 ); } );

	$("#col4_filter").keyup( function() { filterColumn( 3 ); } );
	$("#col4_regex").click(  function() { filterColumn( 3 ); } );
	$("#col4_smart").click(  function() { filterColumn( 3 ); } );

	$("#col5_filter").keyup( function() { filterColumn( 4 ); } );
	$("#col5_regex").click(  function() { filterColumn( 4 ); } );
	$("#col5_smart").click(  function() { filterColumn( 4 ); } );
} );
