dt-example.js:
$.fn.dataTable.ext.type.detect.unshift(
	function ( d ) {
		return /^[\-\d,]+$/.test( d ) ? 'numeric-comma' : null;
	}
);

$.fn.dataTable.ext.type.order['numeric-comma-pre'] = function ( d ) {
	return parseFloat( d.replace(/,/g, '.') ) || 0;
};


$(document).ready(function() {
	$('#example').dataTable();
} );
