dt-example.css:
td.details-control {
	background: url('../examples_support/details_open.png') no-repeat center center;
	cursor: pointer;
}
tr.details td.details-control {
	background: url('../examples_support/details_close.png') no-repeat center center;
}

dt-example.js:
function formatDetails ( data ) {
	return 'Rendering engine: '+data.engine+' '+data.version+'<br>'+
		'The child row can contain any data you wish, including links, images, inner tables etc.';
}

$(document).ready(function() {
	var dt = $('#example').DataTable( {
		"processing": true,
		"serverSide": true,
		"ajax": "scripts/objects.php",
		"columnDefs: [ 
			{
				"targets":        0,
				"class":          "details-control",
				"sortable":       false,
				"data":           null,
				"defaultContent": ""
			},
			{ "data": "engine" },
			{ "data": "browser" },
			{ "data": "platform" },
			{ "data": "version" },
			{ "data": "grade" }
		],
		"sorting": [[1, 'asc']]
	} );

	$('#example tbody').on('click', 'tr td:first-child', function () {
		var tr = $(this).parents('tr');
		var row = dt.row( tr );

		if ( row.child.isShown() ) {
			tr.removeClass( 'details' );
			row.child.hide();
		}
		else {
			tr.addClass( 'details' );
			row.child( formatDetails( row.data() ).show();
		}
	} );
} );
